// Service Worker for Vista AI PWA
// This file will be generated by next-pwa in production

self.addEventListener('install', (event) => {
  console.log('Vista AI: Service Worker installing...');
  self.skipWaiting();
});

self.addEventListener('activate', (event) => {
  console.log('Vista AI: Service Worker activating...');
  event.waitUntil(clients.claim());
});

self.addEventListener('fetch', (event) => {
  // Let browser handle navigation requests
  if (event.request.mode === 'navigate') {
    return;
  }
  
  // Cache API requests
  event.respondWith(
    fetch(event.request).catch(() => {
      // Return offline page if available
      return caches.match('/offline');
    })
  );
});

